import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Container,
  Box,
  Typography,
  Paper,
  Avatar,
  Button,
  Grid,
  TextField,
  Divider,
  IconButton,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Alert,
  Snackbar
} from '@mui/material';
import {
  Edit as EditIcon,
  PhotoCamera as PhotoCameraIcon,
  Save as SaveIcon,
  Cancel as CancelIcon,
  LocationOn as LocationIcon,
  Email as EmailIcon,
  Cake as CakeIcon,
  Work as WorkIcon,
  School as SchoolIcon,
  Favorite as FavoriteIcon,
  Comment as CommentIcon,
  Share as ShareIcon
} from '@mui/icons-material';

function UserProfile() {
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  // Mock user data - in a real app, this would come from your backend
  const [userData, setUserData] = useState({
    username: 'johndoe',
    fullName: 'John Doe',
    email: 'john.doe@example.com',
    location: 'New York, USA',
    bio: 'Passionate about coding, photography, and cooking. Always eager to learn new skills and share knowledge with others.',
    skills: ['JavaScript', 'React', 'Node.js', 'Photography', 'Cooking'],
    joinDate: 'January 2023',
    occupation: 'Software Developer',
    education: 'Bachelor of Computer Science',
    profileImageUrl: 'https://randomuser.me/api/portraits/men/32.jpg'
  });

  // Mock user posts - in a real app, this would come from your backend
  const [userPosts, setUserPosts] = useState([
    {
      id: 1,
      title: 'My First React Project',
      content: 'Just completed my first React project! Here are some tips I learned along the way...',
      imageUrl: 'https://source.unsplash.com/random/600x400/?coding',
      likes: 24,
      comments: 5,
      date: '2023-03-15'
    },
    {
      id: 2,
      title: 'Photography Tips for Beginners',
      content: 'Here are some essential photography tips I wish I knew when I started...',
      imageUrl: 'https://source.unsplash.com/random/600x400/?photography',
      likes: 42,
      comments: 8,
      date: '2023-02-28'
    },
    {
      id: 3,
      title: 'Homemade Pizza Recipe',
      content: 'My secret recipe for the perfect homemade pizza crust...',
      imageUrl: 'https://source.unsplash.com/random/600x400/?pizza',
      likes: 56,
      comments: 12,
      date: '2023-01-10'
    }
  ]);

  const [editedData, setEditedData] = useState({ ...userData });

  useEffect(() => {
    // In a real app, you would fetch the user data from your backend
    // For now, we'll use the mock data
    setEditedData({ ...userData });
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5000000) { // 5MB limit
        setError('Image size should be less than 5MB');
        return;
      }
      setProfileImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedData({
      ...editedData,
      [name]: value
    });
  };

  const handleSave = async () => {
    try {
      // In a real app, you would send the updated data to your backend
      // For now, we'll just update the local state
      
      // If there's a new profile image, you would upload it to your server
      // and get back a URL to store in the user data
      
      setUserData({ ...editedData });
      setIsEditing(false);
      setSuccess('Profile updated successfully!');
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (err) {
      setError('Failed to update profile. Please try again.');
    }
  };

  const handleCancel = () => {
    setEditedData({ ...userData });
    setIsEditing(false);
    setProfileImage(null);
    setImagePreview(null);
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}
      
      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>
          {success}
        </Alert>
      )}
      
      <Grid container spacing={4}>
        {/* Left Column - Profile Info */}
        <Grid item xs={12} md={4}>
          <Paper elevation={3} sx={{ p: 3, borderRadius: 2 }}>
            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mb: 3 }}>
              <Box sx={{ position: 'relative' }}>
                <Avatar
                  src={imagePreview || userData.profileImageUrl}
                  sx={{ width: 150, height: 150, mb: 2 }}
                />
                {isEditing && (
                  <IconButton
                    color="primary"
                    aria-label="upload picture"
                    component="label"
                    sx={{
                      position: 'absolute',
                      bottom: 10,
                      right: 0,
                      bgcolor: 'white',
                      boxShadow: 1,
                      '&:hover': { bgcolor: 'grey.100' }
                    }}
                  >
                    <input
                      hidden
                      accept="image/*"
                      type="file"
                      onChange={handleImageChange}
                    />
                    <PhotoCameraIcon />
                  </IconButton>
                )}
              </Box>
              
              {isEditing ? (
                <TextField
                  fullWidth
                  label="Full Name"
                  name="fullName"
                  value={editedData.fullName}
                  onChange={handleInputChange}
                  sx={{ mb: 2 }}
                />
              ) : (
                <Typography variant="h5" fontWeight="bold">
                  {userData.fullName}
                </Typography>
              )}
              
              <Typography variant="body1" color="text.secondary">
                @{userData.username}
              </Typography>
              
              {!isEditing && (
                <Button
                  variant="outlined"
                  startIcon={<EditIcon />}
                  onClick={() => setIsEditing(true)}
                  sx={{ mt: 2 }}
                >
                  Edit Profile
                </Button>
              )}
            </Box>
            
            <Divider sx={{ my: 2 }} />
            
            <Box sx={{ mb: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <EmailIcon sx={{ mr: 1, color: 'text.secondary' }} />
                {isEditing ? (
                  <TextField
                    fullWidth
                    label="Email"
                    name="email"
                    value={editedData.email}
                    onChange={handleInputChange}
                  />
                ) : (
                  <Typography variant="body1">{userData.email}</Typography>
                )}
              </Box>
              
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <LocationIcon sx={{ mr: 1, color: 'text.secondary' }} />
                {isEditing ? (
                  <TextField
                    fullWidth
                    label="Location"
                    name="location"
                    value={editedData.location}
                    onChange={handleInputChange}
                  />
                ) : (
                  <Typography variant="body1">{userData.location}</Typography>
                )}
              </Box>
              
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <CakeIcon sx={{ mr: 1, color: 'text.secondary' }} />
                <Typography variant="body1">Joined {userData.joinDate}</Typography>
              </Box>
              
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <WorkIcon sx={{ mr: 1, color: 'text.secondary' }} />
                {isEditing ? (
                  <TextField
                    fullWidth
                    label="Occupation"
                    name="occupation"
                    value={editedData.occupation}
                    onChange={handleInputChange}
                  />
                ) : (
                  <Typography variant="body1">{userData.occupation}</Typography>
                )}
              </Box>
              
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <SchoolIcon sx={{ mr: 1, color: 'text.secondary' }} />
                {isEditing ? (
                  <TextField
                    fullWidth
                    label="Education"
                    name="education"
                    value={editedData.education}
                    onChange={handleInputChange}
                  />
                ) : (
                  <Typography variant="body1">{userData.education}</Typography>
                )}
              </Box>
            </Box>
            
            <Divider sx={{ my: 2 }} />
            
            <Typography variant="h6" gutterBottom>
              Skills
            </Typography>
            
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {userData.skills.map((skill, index) => (
                <Chip key={index} label={skill} color="primary" variant="outlined" />
              ))}
            </Box>
            
            {isEditing && (
              <Box sx={{ mt: 3, display: 'flex', gap: 1 }}>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={<SaveIcon />}
                  onClick={handleSave}
                  fullWidth
                >
                  Save Changes
                </Button>
                <Button
                  variant="outlined"
                  color="error"
                  startIcon={<CancelIcon />}
                  onClick={handleCancel}
                  fullWidth
                >
                  Cancel
                </Button>
              </Box>
            )}
          </Paper>
        </Grid>
        
        {/* Right Column - Bio and Posts */}
        <Grid item xs={12} md={8}>
          <Paper elevation={3} sx={{ p: 3, borderRadius: 2, mb: 4 }}>
            <Typography variant="h6" gutterBottom>
              About Me
            </Typography>
            
            {isEditing ? (
              <TextField
                fullWidth
                multiline
                rows={4}
                label="Bio"
                name="bio"
                value={editedData.bio}
                onChange={handleInputChange}
              />
            ) : (
              <Typography variant="body1" paragraph>
                {userData.bio}
              </Typography>
            )}
          </Paper>
          
          <Typography variant="h5" gutterBottom sx={{ mb: 2 }}>
            My Posts
          </Typography>
          
          {userPosts.map((post) => (
            <Card key={post.id} sx={{ mb: 3, borderRadius: 2 }}>
              <CardMedia
                component="img"
                height="200"
                image={post.imageUrl}
                alt={post.title}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {post.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" paragraph>
                  {post.content}
                </Typography>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Typography variant="caption" color="text.secondary">
                    {new Date(post.date).toLocaleDateString()}
                  </Typography>
                  <Box>
                    <Typography variant="caption" sx={{ mr: 2 }}>
                      {post.likes} likes
                    </Typography>
                    <Typography variant="caption">
                      {post.comments} comments
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          ))}
        </Grid>
      </Grid>

      <Snackbar
        open={!!error}
        autoHideDuration={6000}
        onClose={() => setError('')}
      >
        <Alert severity="error" onClose={() => setError('')}>
          {error}
        </Alert>
      </Snackbar>

      <Snackbar
        open={!!success}
        autoHideDuration={6000}
        onClose={() => setSuccess('')}
      >
        <Alert severity="success" onClose={() => setSuccess('')}>
          {success}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default UserProfile;
